import React, { useState, Fragment } from 'react'
import { GoogleMap, Marker, InfoWindow, useLoadScript } from '@react-google-maps/api'

const Map = ({ doods }) => {
  const [ selectedDoodle, setSelectedDoodle ] = useState(null);
  const [mapRef, setMapRef] = useState(null);
  const [center, setCenter] = useState({ lat: 29.968065, lng: -90.111533 });
  const [fakeDoodMarkers, setFakeDoodMarkers] = useState([]);
  const [doodleMarkerCoords, setDoodleMarkerCoords] = useState(center);


  const fakeDoods = [
    {
      id: 0,
      username: "Tom",
      url: "data:image/png;base64,",
      original_url: "https://res.cloudinary.com/jameseaster/image/upload/v1588025558/doodle/a3lubfrynwlctyuorfsp.jpg",
      coords: {lat: 29.901065, lng: -90.141533}
    },
    {
      id: 1,
      username: "Kerry",
      url: "data:image/png;base64,",
      original_url: "https://res.cloudinary.com/jameseaster/image/upload/v1588025558/doodle/a3lubfrynwlctyuorfsp.jpg",
      coords: {lat: 29.951065, lng: -90.121533}
    },
    {
      id: 2,
      username: "Joe",
      url: "data:image/png;base64,",
      original_url: "https://res.cloudinary.com/jameseaster/image/upload/v1588025558/doodle/a3lubfrynwlctyuorfsp.jpg",
      coords: {lat: 29.931065, lng: -90.071533}
    },
  ];

  const { isLoaded } = useLoadScript({
    googleMapsApiKey: process.env.REACT_APP_GOOGLE_KEY
  });

  const logTheCurrentCenter = () =>{
    console.log(mapRef.center.toJSON());
  }

  const createMarker = (e) => {
    setFakeDoodMarkers((fakeDoodMarkers) => (
      [
        ...fakeDoodMarkers,
        {coords: {
          lat: fakeDoodMarkers[fakeDoodMarkers.length - 1].coords.lat + 0.03,
          lng: -90.101533,
        }}
      ]
    )
  )}

  const renderMap = () => {
    return (
      <Fragment>
        <h1>Doodle Map</h1>
        <GoogleMap
          id='example-map'
          mapContainerStyle={{
            height: "70vh",
            width: "80vw",
            margin: "auto",
            "border-radius": "20px",
          }}
          zoom={12}
          center={center}
          onLoad={map => setMapRef(map)}
        >
{/* change fakeDoods to doods when real data is available */}
          {fakeDoods.map((dood) => (
            <Marker
              key={dood.id}
              position={dood.coords}
              onClick={() => {
                setSelectedDoodle(dood)
              }}
            />
          ))}
          {selectedDoodle && (
            <InfoWindow
              position={selectedDoodle.coords}
              onCloseClick={() => {
                setSelectedDoodle(null)
              }}
            >
              <div>
                <h6>{selectedDoodle.username}</h6>
                  <img style={{height: "15vh", width: "auto"}} className="doodle" src={selectedDoodle.url} alt="" />
                  <img style={{height: "15vh", width: "auto"}} className="bg-img" src={selectedDoodle.original_url} alt="" />
              </div>
            </InfoWindow>
          )}
        </GoogleMap>
      </Fragment>
    )
  }
  return isLoaded ? renderMap() : null;
}

export default Map
